import React from "react";
import styles from "./ContactForm.module.css";
import { useState, useSelector } from "react";

export default function ContactForm({ handleChange, handleAddContact }) {
  const [name, setName] = useState();
  const [number, setNumber] = useState();
  const contacts = useSelector((store) => {
    return store.contactReducer;
  });

  function handleChange(evt) {
    switch (evt.target.name) {
      case "name":
        setName(evt.target.value);
        break;

      case "tel":
        setNumber(evt.target.value);
        break;

      default:
        break;
    }
  }

  function handleAddContact() {
    if (
      contacts[0].find((contact) => {
        return contact.name === name;
      })
    ) {
      alert(`${name} is already in contacts`);
    } else {
      const newContact = {
        name: name,
        phone: number,
      };
      dispatch(addContactToServer(newContact));
    }
  }

  return (
    <div className={styles.input__area}>
      <p className={styles.name}>Name</p>
      <input
        onChange={handleChange}
        type="text"
        name="name"
        pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
        title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
        required
      />
      <p className={styles.name}>Phone</p>
      <input type="tel" name="tel" onChange={handleChange} />

      <button
        type="button"
        className={styles.input__btn}
        onClick={handleAddContact}
      >
        Add contact
      </button>
    </div>
  );
}
