import React, { useEffect } from "react";
import styles from "./UserMenu.module.css";
import { useDispatch, useSelector } from "react-redux";
import { logoutAction } from "../../redux/actions";
import { useNavigate } from "react-router-dom";

export default function UserMenu() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const loading = useSelector((state) => {
    return state.loadingReducer;
  });
  const emailLogin = useSelector((state) => {
    return state.userReducer.user.email;
  });
  const emailRefresh = useSelector((state) => {
    return state.userReducer.email;
  });

  function logoutHandler() {
    dispatch(logoutAction());
    navigate("/goit-react-hw-07-phonebook");
  }

  return (
    <div className={styles.login__area}>
      <h2 className={styles.title}>Welcome</h2>
      {!loading && emailLogin && (
        <p className={styles.loginName}>{emailLogin}</p>
      )}
      {emailRefresh && <p className={styles.loginName}>{emailRefresh}</p>}
      <button type="button" className={styles.button} onClick={logoutHandler}>
        Logout
      </button>
    </div>
  );
}
